package main

import (
	"fmt"
	"testing"
)

func Test(t *testing.T) {

	t.Run("should correctly identiy the position of the first marker of 4 unique chars where first encountered window is unique", func(t *testing.T) {
		var signal = "abcd"
		got := getMarkerPos(signal, 4)
		want := 4
		if got != want {
			t.Errorf("got - %d, want - %d", got, want)
		}
	})

	t.Run("should correctly identiy the position of the first marker of 4 unique chars", func(t *testing.T) {
		var signal = ""
		got := getMarkerPos(signal, 4)
		want := 1034
		if got != want {
			t.Errorf("got - %d, want - %d", got, want)
		}
	})

	t.Run("should correctly identiy the position of the first marker of 14 unique chars", func(t *testing.T) {
		var signal = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
		fmt.Println("len", len(signal), string(signal[22]))
		got := getMarkerPos(signal, 14)
		want := 19
		if got != want {
			t.Errorf("got - %d, want - %d", got, want)
		}
	})
}
